package main

import (
	"fmt"
	"strings"
)

const (
	TRANSMISSION = ""
)

func main() {
	fmt.Println(findMarker(TRANSMISSION))
}

// Find the first unique four character sequence index
func findMarker(transmission string) int {
	markerIndex := 0
	for i := 0; i < len(transmission); i++ {
		if len(transmission) > i+14 {
			nextFourteen := transmission[i : i+14]
			unique := true
			for _, v := range nextFourteen {
				if strings.Count(nextFourteen, string(v)) > 1 {
					unique = false
				}
			}
			if unique {
				return i + 14
			}
		}
	}
	return markerIndex
}
